import './Home.css'
import React from 'react';
import {useState, useEffect} from 'react';
import {useDispatch, useSelector} from 'react-redux';
import { getDogs, filterDogsByRaza, filterDogsByStatus } from '../actions';
import { Link } from 'react-router-dom';
import Card from './Card';
import Paginado from './Paginado';


export default function Home(){
    const dispatch = useDispatch()
    const allDogs = useSelector ((state) => state.dogs)
    const [currentPage,setCurrentPage] = useState(1)
    const [dogsPerPage,setDogsPerPage] = useState(8)
    const indexOfLastDogs = currentPage * dogsPerPage 
    const indexOfFirstDogs = indexOfLastDogs - dogsPerPage 
    const currentDogs = allDogs.slice(indexOfFirstDogs,indexOfLastDogs)
    
    const paginado = (pageNumber) => {
        setCurrentPage(pageNumber)
    }

    console.log(paginado)
    
    useEffect(()=>{
        dispatch(getDogs())
    },[dispatch])

    function handleClick(e){
    e.preventDefault();
    dispatch(getDogs());
    }

    function handleFilterRaza(e){
    dispatch(filterDogsByRaza(e.target.value))
    }
    function handleFilterStatus(e){
        dispatch(filterDogsByStatus(e.target.value))
        }

