        <div className = 'Home'>
            <Link to='/dog'>Añadir raza</Link>
            <h1>Biblioteca perruna</h1>
            <button onClick ={e=> {handleClick(e)}}>
                Volver a cargar todos los perritos
            </button>
            <div>
                <select>
                    <option value='asc'>Ascendente</option>
                    <option value='desc'>Descendente</option>
                </select>
                <select onChange = {e => handleFilterStatus(e)}>
                    <option value='All'>Todos</option>
                    <option value='created'>Creados</option>
                    <option value='api'>Existente</option>
                </select>
                <select>
                    <option id= 'Raza' value='AllRaza'>Raza</option>
                    <option value='name'>Raza</option>
                </select>
                <p>Filtrar por temperamento:</p>
                        <select onChange={temperamentChange} >
                             <option value={''} >Seleccionar filtro</option>                               
