                             <option value={el.name}>{el.name}</option>                               
                            ))}
                        </select>

                <Paginado
            dogsPerPage={dogsPerPage}
            allDogs={allDogs.length}
            paginado={paginado}
            />

                  
    {   currentDogs.map((c)=>{
        return(
            <React.Fragment>
                <Link to = {'/home/'+c.id}>
               <Card name = {c.name} image = {c.image} temperament = {c.temperament} key = {c.id}/>  
               </Link>  
            </React.Fragment>       
        )
    })}
              
            </div>
            
        </div>
    )
    }
