import { useDispatch, useSelector } from 'react-redux';
import React from 'react';
import axios from 'axios';
import { useState, useEffect } from 'react';
import { filterTemperamentsByStatus } from '../actions'




function Buscar() {
    const dispatch = useDispatch()
    const temperaments = useSelector((state) => state.temperament);

    //const [temperamentSelected, setTemperamentSelected] = useState("")
    useEffect(() => {
        axios.get(`http://localhost:3001/temperaments`) 
        .then((res) => {
            dispatch ({
                type: "FILTRO_TEMPERAMENT",
                payload: res.data
            })
        })
        .catch((error) => {
        console.log(error);
    });        
    }, [])

    const temperamentChange = (e) => {
        e.preventDefault();
        dispatch(filterTemperamentsByStatus(e.target.value))
    }


    return (

        <div>
            <p>Filtrar por temperamento:</p>
            <select onChange={temperamentChange} >
                <option value={''} >Seleccionar temperamento</option>
                {temperaments.map((el) => (
                    <option value={el.name}>{el.name}</option>
                ))}
            </select>

        </div>

    )
}



export default Buscar;
