const initialState = {
    dogs: [],
    temperaments:[]
}
export function rootReducer(state = initialState, action) {
    switch (action.type) {
        case 'GET_DOGS':
            return {
                ...state,
                dogs: action.payload
            }
        case 'FILTER_RAZA':
            const allDogs = state.dogs
            const statusFilter = action.payload === 'All' ? allDogs : allDogs.filter(c => c.state === action.payload)
            return {
                ...state,
                dogs: statusFilter
            }
        case 'FILTER_CREATED':
            const allDogs2 = state.dogs
            const createdFilter = action.payload === 'created' ? allDogs2.filter(c => c.createdInDb) : allDogs2.filter(c => !c.createdInDb)
            return {
                ...state,
                dogs: action.payload === 'All' ? state.allDogs : createdFilter
            }


    default:
        return state;
            }
    }
    export default rootReducer;
