import { useState, useEffect } from 'react';
import {  useDispatch, useSelector } from 'react-redux';
import React from 'react';
import {filterTemperamentsByStatus} from '../actions'
import {filterDogsByRaza} from '../actions'



function Buscar() {
    const dispatch = useDispatch()
    const temperaments = useSelector((state) => state.dogs);
    const dogis = useSelector((state) => state.dogs);
    //const [temperamentSelected, setTemperamentSelected] = useState("")
 

    const temperamentChange = (e) => {
    e.preventDefault();
    dispatch(filterTemperamentsByStatus(e.target.value)) 
    }
    const dogsChange = (e) => {
        e.preventDefault();
        dispatch(filterDogsByRaza(e.target.value)) 
        }

    return (

        <div>
            <p>Filtrar por temperamento:</p>
            <select onChange={temperamentChange} >
                <option value={''} >Seleccionar temperamento</option>
                {temperaments.map((el) => (
                    <option value={el.name}>{el.name}</option>
                ))}
            </select>
            <select onChange={dogsChange} >
                <option value={''} >Seleccionar raza</option>
                {dogis.map((el) => (
                    <option value={el.name}>{el.name}</option>
                ))}
            </select>
        </div>

    )
}
export default Buscar;
