import './Home.css'
import React from 'react';
import {useState, useEffect} from 'react';
import {useDispatch, useSelector} from 'react-redux';
import { getDogs, filterDogsByRaza, fil } from '../actions';
import { Link } from 'react-router-dom';
import Card from './Card';
import Paginado from './Paginado';


export default function Home(){
    const dispatch = useDispatch()
    const allDogs = useSelector ((state) => state.dogs)
    const [currentPage, setCurrentPage] = useState(1)
    const [dogsPerPage, setDogPerPage] = useState(8)
    const indexOfLastDog = currentPage * dogsPerPage
    const indexOfFirstDog = indexOfLastDog - dogsPerPage
    const currentDogs = allDogs.slice(indexOfFirstDog, indexOfLastDog)

    const paginado = (pageNumber) => {
        setCurrentPage(pageNumber)
    }

    console.log(paginado)
    
    useEffect(()=>{
        dispatch(getDogs())
    },[dispatch])

    function handleClick(e){
    e.preventDefault();
    dispatch(getDogs());
    }

    function handleFilterStatus(e){
    dispatch(filterDogsByRaza(e.target.value))
    }

    const namesDogs = currentDogs.name;
    console.log(namesDogs);
    return (
        <div className = 'Home'>
            <Link to='/dog'>AÃ±adir raza</Link>
            <h1>Biblioteca perruna</h1>
            <button onClick ={e=> {handleClick(e)}}>
                Volver a cargar todos los perritos
            </button>
            <div>
                <select>
                    <option value='asc'>Ascendente</option>
                    <option value='desc'>Descendente</option>
                </select>
                <select>
                    <option value='All'>Todos</option>
                    <option value='created'>Creados</option>
                    <option value='api'>Existente</option>
                </select>
                <select>
                    <option id= 'Raza' value='AllRaza'>Raza</option>
                    <option value='name'>Raza</option>
                </select>

    <Paginado
    dogsPerPage= {dogsPerPage}
    totalDogs={allDogs.lenght}
    paginado = {paginado}
    />

                  
    {   currentDogs.map((c)=>{
        return(
            <React.Fragment>
                <Link to = {'/home/'+c.id}>
               <Card name = {c.name} image = {c.image} temperament = {c.temperament} key = {c.id}/>  
               </Link>  
            </React.Fragment>       
        )
    })}
              
            </div>
            
        </div>
    )
    }
