import React from "react";
import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  getDogs,
  orderbyName,
  orderbyWeight,
  filterDogsCreated,
  filterDogTemp,
  getTemperaments,
} from "../actions";
import { Link } from "react-router-dom";
import Card from "./Card";
import Paged from "./Paged";
import SearchBar from "./SearchBar";



export default function Home() {
    const dispatch = useDispatch()
    const allDogs = useSelector((state) => state.dogs)
    const [order, setOrder] = useState('')
    const [orderr, setOrderr] = useState("");//cambiar nombres
    const [currentPage, setCurrentPage] = useState(1)
    const [dogsPerPage, setDogsPerPage] = useState(8)
    const indexOfLastDogs = currentPage * dogsPerPage
    const indexOfFirstDogs = indexOfLastDogs - dogsPerPage
    const currentDogs = allDogs.slice(indexOfFirstDogs, indexOfLastDogs)
    //const [temperamentSelected, setTemperamentSelected] = useState("")

    const paginado = ((pageNumber) => {
        setCurrentPage(pageNumber)
    })

    const allTemp = useSelector((state) => state.temperaments);

    useEffect(() => {
        dispatch(getDogs())
    }, [dispatch])

    function handleClick(e) {
        e.preventDefault();
        dispatch(getDogs());
    }

    function handleFilterCreated(e) {
        //declaro una funci칩n que es un handle del filter del dog creado o de api
        e.preventDefault(); //esta funcion es la que paso en el select y cuando (e) se modifique ejecuta esta funci칩n
        dispatch(filterDogsCreated(e.target.value)); //despacho la acci칩n llamada filterDogsCreated y accedo al valor de cada una de las opciones
      } //de value con el e.target.value - dependiendo de cu치l clickea el usuario
    
      
    return (
        <div className='Home'>
            <Link to='/dog'>A침adir raza</Link>
            <h1>Biblioteca perruna</h1>
            <button onClick={e => { handleClick(e) }}>
                Volver a cargar todos los perritos
            </button>
            <div>
                <select>
                    <option value='asc'>Ascendente</option>
                    <option value='desc'>Descendente</option>
                </select>
                <select onChange={e => handleFilterDogs(e)}>
                    <option value='All'>Todos</option>
                    <option value='created'>Creados</option>
                    <option value='api'>Existentes</option>
                </select>

                
                <Raza/>
                <Paginado
                    dogsPerPage={dogsPerPage}
                    allDogs={allDogs.length}
                    paginado={paginado}
                />
                
                {currentDogs.map((c) => {
                    return (
                        <React.Fragment>
                            
                            <div className='main'>
                                <div>
                                <Link to={'/home/' + c.id}>
                                    <div className = 'content-icon'><Card name={c.name} image={c.image} key={c.id} /></div>
                                </Link>
                                </div>
                            </div>
                        </React.Fragment>
                    )
                })}

            </div>

        </div >
    )
}
