const initialState ={
    dogs :[]
}
 export function rootReducer(state = initialState, action){
    switch(action.type) {
        case 'GET_DOGS':
            return {
                ...state,
                dogs: action.payload
    }
    case 'FILTER_RAZA':
        const allDogs = state.dogs
        const statusFilter = action.payload === 'All' ? allDogs : allDogs.filter(c => c.status === action.payload) 
        return {
            ... state,
                        dogs: statusFilter
        }
    default:
        return state;
    }
}
export default rootReducer;
