import { FILTER_TEMPERAMENT,
    FILTER_RACE, 
    FILTER_CREATED} from '../actions/index';


const initialState = {
    dogs: [],
    allDogs: [],
    allTemperaments: [],
    detail: [],
    
}
export function rootReducer(state = initialState, action) {
    switch (action.type) {
        case 'GET_DOGS':
            return {
                ...state,
                dogs: action.payload,
                allDogs: action.payload,
                detail: action.payload
            }

            case 'GET_TEMPERAMENTS':
                return{
                    ...state,
                    occupations: action.payload
                } 

                case 'POST_CHARACTER':
                    return {
                        ...state
                    } 
        case FILTER_RACE:
            const allDogs = state.allDogs
            //const statusFilter = action.payload === 'Name' ? allDogs : allDogs.filter(c => c.state === action.payload)
            const statusFilter = allDogs.filter(c => c.state === action.payload)
            return {
                ...state,
                dogs: statusFilter
            }
        case FILTER_TEMPERAMENT:
            const allTemperaments = state.temperament
            //const statusTemp = action.payload === 'All' ? allTemperaments : allTemperaments.filter(c => c.state === action.payload)
            const statusTemp = allTemperaments.filter(c => c.temperament === action.payload)
            return {
                ...state,
                temperaments: statusTemp
            }

            case FILTER_CREATED:
                            //const statusTemp = action.payload === 'All' ? allTemperaments : allTemperaments.filter(c => c.state === action.payload)
                const createdFilter = action.payload === 'created' ? state.allDogs.filter(c => c.createdInDb) : state.allDogs.filter(c => !c.createdInDb)
                return {
                    ...state,
                    dogs: action.payload === 'All' ? state.allDogs : createdFilter
                }

                case 'ORDER_BY_NAME':
                    const sortedarr = action.payload === 'asc' ?
                    state.allDgos.sort(function(a,b){
                        if (a.name > b.name){
                            return 1;
                        }
                        if (b.name > a.name){
                            return -1;
                        }
                        return 0
                    }) :
                    state.allcharacters.sort(function(a,b){
                        if (a.name > b.name){
                            return -1;
                        }
                        if (b.name > a.name){
                            return 1;
                        }
                        return 0
                    }) 
                    return{
                        ...state,
                        characters:  sortedarr
                    } 

        default:
            return state;
    }
}
export default rootReducer;
