import { FILTER_TEMPERAMENT,FILTER_RAZA, GET_TEMPERAMENTS} from '../actions/index';


const initialState = {
    dogs: [],
    temperaments: []
}
export function rootReducer(state = initialState, action) {
    switch (action.type) {
        case 'GET_DOGS':
            return {
                ...state,
                dogs: action.payload
            }
        case GET_TEMPERAMENT:
            return {
                ...state,
                temperaments: action.payload
            }

        case FILTER_RAZA:
            const allDogs = state.dogs
            const statusFilter = action.payload === 'All' ? allDogs : allDogs.filter(c => c.state === action.payload)
            return {
                ...state,
                dogs: statusFilter
            }
        case FILTER_TEMPERAMENT:
            const allTemperaments = state.temperament
            const statusTemp = action.payload === 'All' ? allTemperaments : allTemperaments.filter(c => c.state === action.payload)
            //const statusTemp = allTemperaments.filter(c => c.temperament === action.payload)
            return {
                ...state,
                temperaments: statusTemp
            }

        default:
            return state;
    }
}
export default rootReducer;
